mapper.select:
SELECT ROWNUM no, CASE WHEN DISTANCE < 1 THEN
RTRIM(TO_CHAR(DISTANCE*1000 , 'FM9990D99'),'.')||'m' ELSE DISTANCE||'km' END distance,T.* , S.* , (SELECT COUNT(*) FROM SENIOR_BMARK B WHERE B.KEY = S.KEY ) SENIOR_LIKE_NUM from ( SELECT ROWNUM no , C.DISTANCE , S.KEY , S.SENIOR_ROADADDRESS FROM SENIOR S LEFT OUTER JOIN (SELECT KEY , DISTNACE_WGS84(#{senior_latitude}, #{senior_longitude}, SENIOR_LATITUDE, SENIOR_LONGITUDE) AS DISTANCE FROM SENIOR S ) C ON S.KEY=C.KEY ORDER BY C.DISTANCE ASC ) T INNER JOIN SENIOR S ON T.KEY = S.KEY
WHERE ROWNUM < #{zoom_level}

mapper.delete:
delete from SENIOR_BMARK
where key = #{key} and
member_id=#{member_id}

mapper.select:
select key
from SENIOR_BMARK
where key=#{key} and member_id=#{member_id}
